{-# LANGUAGE OverloadedStrings   #-}
{-# LANGUAGE ScopedTypeVariables #-}

module Lib
    ( run
    ) where

import qualified Intcode as I
import qualified Data.Text           as T
import qualified Data.Text.IO        as TIO
import qualified Data.Char as C

type Coor = (Int, Int)

-- run :: IO ()
run = do
  input :: [Int] <- fmap read . fmap T.unpack . T.splitOn "," <$> TIO.readFile "src/input.txt"
  let prg = input ++ (replicate 0000 0)
  -- let res = I.runBasic prg
  let spaceMap :: [[Char]] = toChar mmap
  print $ calc $ findIntersections spaceMap

calc xs =
  sum $ map (\(v,(x,y)) -> x*y) xs

toCoors :: [[Char]] -> [(Char, Coor)]
toCoors arr = [(v, (x, y)) | (row, y) <- zip arr [0..], (v, x) <- zip row [0..]]

findIntersections :: [[Char]] -> [(Char, Coor)]
findIntersections spaceMap =
  filter (isInterSection spaceMap) $ toCoors spaceMap


isInterSection spaceMap (v, coors)  = all (\(x,y) -> (spaceMap!!y!!x) == '#') $ neighbours coors



neighbours :: Coor -> [Coor]
neighbours (x,y) =
  filter (\(x',y') -> x'>=0 && y'>=0 && x'<width && y'<height)[(x-1, y), (x+1, y), (x, y+1), (x,y-1), (x,y)]

toChar :: [Int] -> [[Char]]
toChar = lines . map C.chr











width = length $ takeWhile (\x -> x /= 10) mmap
height = (length $ filter ((==) 10) mmap) -1

mmap :: [Int]
